items.css:
.potion-btn {
  padding: 12px 24px;
  background: #ff7675;
  color: white;
  border: 4px solid #d63031;
  border-radius: 4px;
  font-family: sans-serif;
  font-weight: bold;
  cursor: pointer;
  box-shadow: 0 4px 0 #d63031;
}
.potion-btn:active {
  transform: translateY(2px);
  box-shadow: 0 2px 0 #d63031;
}

.lemon-loader {
  width: 40px;
  height: 40px;
  border: 4px solid #ffe66d;
  border-top: 4px solid transparent;
  border-radius: 50%;
  animation: spin 1s linear infinite;
}
@keyframes spin { 0% { transform: rotate(0deg); } 100% { transform: rotate(360deg); } }

/* THE WORST VOLUME CONTROL EVER MADE */

.volume-wrapper {
  width: 400px;
  height: 40px;
  margin: 100px;
  transform: rotate(180deg) skew(12deg, -8deg);
  background: repeating-linear-gradient(
    45deg,
    #ff00ff,
    #ff00ff 5px,
    #00ffff 5px,
    #00ffff 10px
  );
  animation: shake 0.3s infinite;
  cursor: not-allowed;
}

.volume-bar {
  height: 100%;
  width: 63%;
  background: linear-gradient(
    to right,
    lime,
    red,
    yellow,
    cyan,
    magenta
  );
  filter: blur(1.5px) contrast(300%);
  transition: width 0.05s linear;
}

.volume-wrapper:hover .volume-bar {
  width: 17%;
  transform: scaleY(1.4) rotate(7deg);
}

@keyframes shake {
  0% { transform: translate(0, 0) rotate(180deg); }
  25% { transform: translate(3px, -2px) rotate(182deg); }
  50% { transform: translate(-4px, 3px) rotate(179deg); }
  75% { transform: translate(2px, 4px) rotate(181deg); }
  100% { transform: translate(0, 0) rotate(180deg); }
}
